import React, { Component } from 'react';
import styles from './PageFooter.module.css';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Pagination from 'react-bootstrap/Pagination';

class PageFooter extends Component{
  render() {
    console.log("footer: " +this.props.index);
    return (
      <Row>
          <Col></Col>
          <Col>
            <div>
              {/* style={{ display: "flex", justifyContent: "center" }}> */}
              <Pagination className={styles.PageFooter}>
                {(() => {
                  let items = [];
                  for (let i = 0; i < this.props.pageCount; i++) {
                    if (i == this.props.index) {
                      items.push(<Pagination.Item key={i} active>X</Pagination.Item>);
                    } else {
                      items.push(<Pagination.Item key={i} disabled>x</Pagination.Item>);
                    }
                  }
                  return items;
                })()}
              </Pagination>
            </div>
          </Col>
          <Col></Col>
      </Row>

    );
  }
}

export default PageFooter;
