import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import AppContext from '../AppContext/AppContext';

const IntroPage  = () => {
  const myContext = useContext(AppContext);
  console.log("intro: "+myContext.name);

  // render() {

  function nextIndex() {
    console.log(myContext.index);
    myContext.setIndexValue(myContext.index+1);
    console.log(myContext.index);
  }

  function prevIndex() {
    console.log(myContext.index);
    myContext.setIndexValue(myContext.index-1);
    console.log(myContext.index);
  }

  return (
    // <AppContext.Consumer>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <h3><Form.Label>What is your name?</Form.Label></h3>
          <Form.Control type="name" placeholder={myContext.name} onChangeText={console.log(myContext.name)}/>
        </Form.Group>
      </Form>
    // </AppContext.Consumer>
  );
  // }
}
export default IntroPage;
