import React, { useState, Component, useContext, useEffect } from 'react'
import PropTypes from 'prop-types';
import styles from './PageCardHolder.module.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import AppContext from '../AppContext/AppContext';
import { useNavigate } from "react-router-dom";

import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

const PageCardHolder = (props) => {
  const myContext = useContext(AppContext);
  let navigate = useNavigate();
  const inComponents = props.components;
  function navigateToRoute () {
    navigate(inComponents[myContext.index].path);
  }
  function nextIndex() {
    myContext.setIndexValue(++myContext.index);
    if (myContext.index >= (myContext.indexMax-1)) {
      myContext.setIndexValue(myContext.indexMax-1);
    } 
    navigateToRoute();
  }

  function prevIndex() {
    console.log("previous Index: " + myContext.index);
    myContext.setIndexValue(myContext.index-1);
    console.log("current Index: " + myContext.index);
    if (myContext.index <= 0) {
      myContext.setIndexValue(0);
    }
    navigateToRoute();
  }

  useEffect(() => {
    console.log("Component did mount! index:" + props.index);
    navigateToRoute();
  }, []);

  return (
    <Card className="text-center">
      <Card.Header>
        <Row>
            <Col xs={1}><Button onClick={prevIndex}>{'<'}</Button></Col>
            <Col>
              <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTEBw0f7BqKsBDTpnaeMR8YVllP-qqjw4zIw&usqp=CAU" alt="car"
                />
            </Col>
            <Col xs={1}><Button onClick={nextIndex}>{'>'}</Button></Col>
        </Row>
      </Card.Header>
      <Card.Body>
        <Row>
          <Col></Col>
          <Col xs={4}>
            <Routes>
              {(() => {
                let routes = []
                for(var i=0; i < inComponents.length; i++) {
                  routes.push(
                    <Route
                      key={inComponents[i].index}
                      path={inComponents[i].path} 
                      element= {inComponents[i].component} 
                    />
                  )
                }
                return routes;
              })()}
            </Routes>
          </Col>
          <Col></Col>
        </Row>
      </Card.Body>
    </Card>
  );
}

export default PageCardHolder;
