import React, { useState, Component, useContext } from 'react';
import styles from './PageHeader.module.css';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import AppContext from '../AppContext/AppContext';
import { Link } from 'react-router-dom'
import {
  BrowserRouter as Router,
  Routes,
  Route } from "react-router-dom";

const PageHeader = (props) => {
  const myContext = useContext(AppContext);
  const setClickedLinkIndex = () => {
    myContext.setIndexValue(++myContext.index);
  }
  return (
    <Navbar bg="light" expand={false}>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="text-center" defaultActiveKey="/">
          {(() => {
            let components = [];
            for(var i=0; i < props.components.length; i++) {
              components.push(
                <Link 
                  to={props.components[i].path} 
                  key={props.components[i].name} 
                  onClick={setClickedLinkIndex()}
                >
                  {props.components[i].name.replace(/([A-Z])/g, ' $1').trim()}
                </Link>
              )
            }
            return components;
          })()}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default PageHeader;
