import React, { useState, Component, useContext } from 'react';
import PropTypes from 'prop-types';
import styles from './PageCardHolder.module.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import AppContext from '../AppContext/AppContext';

import {
  BrowserRouter as Router,
  Routes,
  Route } from "react-router-dom";

const PageCardHolder = (props) => {
  const myContext = useContext(AppContext);
  console.log("PageCardHolder: "+myContext.name);
  console.log(myContext);

  function nextIndex() {
    console.log("nextIndex");
    console.log(myContext.index);
    myContext.setIndexValue(myContext.index+1);
    console.log(myContext.index);
  }

  function prevIndex() {
    console.log("prevIndex");
    console.log(myContext.index);
    myContext.setIndexValue(myContext.index-1);
    console.log(myContext.index);
  }
    return (
      // <AppContext.Consumer>
      <Router>
        <Card className="text-center">
          <Card.Header>
            <Row>
                <Col xs={1}>
                  <Button onClick={prevIndex()}>
                    {'<'}
                  </Button>
                </Col>
                <Col>
                  <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTEBw0f7BqKsBDTpnaeMR8YVllP-qqjw4zIw&usqp=CAU" alt="car"
                    />
                </Col>
                <Col xs={1}>
                  <Button onClick={nextIndex()}>
                    {/* //myContext.setIndexValue(myContext.index+1)}> */}
                    {'>>'}
                  </Button>
                </Col>
            </Row>
          </Card.Header>
          <Card.Body>
            <Row>
              <Col>
              </Col>
              <Col xs={4}>
                <Routes>
                  {(() => {
                    let routes = []
                    for(var i=0; i < props.components.length; i++) {
                      routes.push(
                        <Route exact 
                        path={props.components[i].path} 
                        element= {props.components[i].component} />
                      )
                    }
                    return routes;
                  })()}
                </Routes>
              </Col>
              <Col>
              </Col>
            </Row>
          </Card.Body>
        </Card>
      </Router>
    );
}

export default PageCardHolder;
