import React from 'react';
import styles from './PageFooter.module.css';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Pagination from 'react-bootstrap/Pagination';

const PageFooter = (props) => {
  return (
    <Row>
        <Col></Col>
        <Col>
          <div>
            {/* style={{ display: "flex", justifyContent: "center" }}> */}
            <Pagination className={styles.PageFooter} >
              {(() => {
                let items = [];
                for (let i = 0; i < props.pageCount; i++) {
                  if (i === props.index) {
                    items.push(<Pagination.Item key={i} active style={{border:"none", background:"yellow"}}> <h1>.</h1></Pagination.Item>);
                  } else {
                    items.push(<Pagination.Item key={i} disabled style ={{border:"none", background:"yellow"}}><h3>.</h3></Pagination.Item>);
                  }
                }
                return items;
              })()}
            </Pagination>
          </div>
        </Col>
        <Col></Col>
    </Row>

  );
}

export default PageFooter;
