import React, { useState, Component, useContext } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import AppContext from '../AppContext/AppContext';

const QuestionsPage = () => {
  const myContext = useContext(AppContext);
  const checkSelected= (event) => {
    console.log(event.target.id);
    if (myContext.optionsValue.has(event.target.id)) {
      myContext.setOptionsValue(myContext.optionsValue.delete(event.target.id));
    } else {
      myContext.setOptionsValue(myContext.optionsValue.add(event.target.id));
    }
    console.log(myContext.optionsValue);
  }
  // const myContext = useContext(AppContext);
  return (
    <Form style={{textAlign:"left", left:0}} >
      <Form.Check 
        type="checkbox"
        id="OptionA"
        label="Option A"
        onChange={checkSelected}
      />
      <Form.Check 
        type="checkbox"
        id="OptionB"
        label="Option B"
        onChange={checkSelected}
      />
      <Form.Check 
        type="checkbox"
        id="OptionC"
        label="Option C"
        onChange={checkSelected}
      />
      <Form.Check 
        type="checkbox"
        id="Option"
        label="Option D"
        onChange={checkSelected}
      />
    </Form>
  );
}

export default QuestionsPage;
